import { useState, useEffect } from 'react';
import imagesData from '../assets/images.json';

const useCanvas = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Asegurarnos de que las imágenes se distribuyan por todo el canvas
    const positionedImages = imagesData.images.map((image, index) => {
      console.log('Cargando imagen:', image.url); // Debug
      return {
        ...image,
        x: Math.random() * 15000 + 2500, // Distribuimos entre 2500 y 17500
        y: Math.random() * 15000 + 2500, // Distribuimos entre 2500 y 17500
        width: 400,  // Aseguramos que todas las imágenes tengan un tamaño
        height: 300, // Aseguramos que todas las imágenes tengan un tamaño
        zIndex: index
      };
    });

    console.log('Imágenes posicionadas:', positionedImages);
    setImages(positionedImages);
  }, []);

  return { images };
};

export default useCanvas;
