import styled from 'styled-components';

export const Viewport = styled.div`
  width: 100vw;
  height: 100vh;
  overflow: hidden;
  position: relative;
  background: #f0f0f0;
`;

export const CanvasContainer = styled.div`
  width: 20000px;
  height: 20000px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform-origin: center;
  cursor: grab;

  &:active {
    cursor: grabbing;
  }
`;
