import React from 'react';
import { useGesture } from 'react-use-gesture';
import * as S from './Image.styles';

const Image = ({ image, onDrag, onSelect }) => {
  const bind = useGesture({
    onDrag: ({ movement, event }) => {
      event.preventDefault();
      if (movement && Array.isArray(movement)) {
        onDrag(movement, image.id);
      }
    },
    onMouseDown: (event) => {
      event.preventDefault();
      onSelect(image.id);
    }
  }, {
    drag: {
      initial: [0, 0]
    }
  });

  return (
    <S.ImageWrapper
      {...bind()}
      style={{
        transform: `translate(${image.x}px, ${image.y}px)`,
        zIndex: image.zIndex || 1,
        width: image.width,
        height: image.height
      }}
    >
      <S.Image src={image.url} alt={image.title} draggable={false} />
      <S.ImageInfo>
        <S.Title>{image.title}</S.Title>
        <S.Tags>{image.tags.join(', ')}</S.Tags>
      </S.ImageInfo>
    </S.ImageWrapper>
  );
};

export default Image;
