import styled from 'styled-components';

export const Viewport = styled.div`
  width: 100vw;
  height: 100vh;
  overflow: hidden;
  position: relative;
  background: #f0f0f0;
`;

export const CanvasContainer = styled.div`
  width: 20000px;
  height: 20000px;
  position: absolute;
  cursor: grab;
  touch-action: none;
  user-select: none;

  /* Patrón cuadriculado */
  background-image: linear-gradient(#ccc 1px, transparent 1px),
                    linear-gradient(90deg, #ccc 1px, transparent 1px);
  background-size: 100px 100px;
  background-color: #f8f8f8;

  &:active {
    cursor: grabbing;
  }

  /* Líneas más gruesas cada 500px */
  background-image: 
    linear-gradient(#ccc 1px, transparent 1px),
    linear-gradient(90deg, #ccc 1px, transparent 1px),
    linear-gradient(#999 2px, transparent 2px),
    linear-gradient(90deg, #999 2px, transparent 2px);
  background-size: 
    100px 100px,
    100px 100px,
    500px 500px,
    500px 500px;
`;

export const CenterMarker = styled.div`
  position: absolute;
  top: 10000px;
  left: 10000px;
  width: 20px;
  height: 20px;
  background: red;
  border-radius: 50%;
  transform: translate(-50%, -50%);
`;

export const Coordinates = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 10px;
  border-radius: 5px;
  font-family: monospace;
  z-index: 1000;
`;
