import { useState, useEffect } from 'react';
import { initializeImagePositions } from '../utils/position';
import imagesData from '../assets/images.json';

const useCanvas = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Asegurarnos de que las im치genes se distribuyan por todo el canvas
    const positionedImages = imagesData.images.map((image, index) => ({
      ...image,
      x: Math.random() * 19000, // Dejamos 1000px de margen
      y: Math.random() * 19000, // Dejamos 1000px de margen
      zIndex: index
    }));

    console.log('Im치genes posicionadas:', positionedImages); // Para debugging
    setImages(positionedImages);
  }, []);

  return { images };
};

export default useCanvas;
