import { useState, useEffect } from 'react';
import imagesData from '../assets/images.json';

const useCanvas = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    const CANVAS_CENTER = 10000;
    const SPACING = 800; // Aumentado el espacio entre imágenes

    const generateSpiralPosition = (index) => {
      const goldenAngle = Math.PI * (3 - Math.sqrt(5));
      const distance = SPACING * Math.sqrt(index);
      const angle = index * goldenAngle;

      return {
        x: CANVAS_CENTER + distance * Math.cos(angle),
        y: CANVAS_CENTER + distance * Math.sin(angle)
      };
    };

    // Usar URLs de prueba temporales para verificar que todo funciona
    const testImages = [
      {
        id: "1",
        url: "https://picsum.photos/400/300",
        width: 400,
        height: 300,
        title: "Imagen de prueba 1",
        tags: ["test", "imagen", "prueba"],
        date: "2024-03-15",
        author: "Autor 1"
      },
      {
        id: "2",
        url: "https://picsum.photos/401/301",
        width: 400,
        height: 300,
        title: "Imagen de prueba 2",
        tags: ["test", "imagen", "prueba"],
        date: "2024-03-15",
        author: "Autor 2"
      },
      {
        id: "3",
        url: "https://picsum.photos/402/302",
        width: 400,
        height: 300,
        title: "Imagen de prueba 3",
        tags: ["test", "imagen", "prueba"],
        date: "2024-03-15",
        author: "Autor 3"
      }
    ];

    // Usar imágenes de prueba temporalmente
    const positionedImages = testImages.map((image, index) => {
      const position = generateSpiralPosition(index);
      console.log(`Posicionando imagen ${image.id} en:`, position); // Debug log

      return {
        ...image,
        x: position.x,
        y: position.y,
        width: 400,
        height: 300,
        zIndex: index
      };
    });

    console.log('Imágenes posicionadas:', positionedImages);
    setImages(positionedImages);
  }, []);

  return { images };
};

export default useCanvas;
