import styled from 'styled-components';

export const CanvasContainer = styled.div`
  width: 5000px;
  height: 5000px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  cursor: move;
  background: ${({ theme }) => theme.colors.background};

  &:active {
    cursor: grabbing;
  }
`;
