import { useState, useEffect } from 'react';
import imagesData from '../assets/images.json';

const useCanvas = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    const CANVAS_CENTER = 10000; // Centro del canvas de 20000x20000
    const SPACING = 600; // Espacio entre imágenes

    // Función para generar posiciones en espiral
    const generateSpiralPosition = (index) => {
      // Ángulo áureo, crea una distribución natural
      const goldenAngle = Math.PI * (3 - Math.sqrt(5));

      // Distancia desde el centro, aumenta con cada imagen
      const distance = SPACING * Math.sqrt(index);

      // Ángulo para esta imagen
      const angle = index * goldenAngle;

      return {
        x: CANVAS_CENTER + distance * Math.cos(angle),
        y: CANVAS_CENTER + distance * Math.sin(angle)
      };
    };

    // Posicionar imágenes en espiral desde el centro
    const positionedImages = imagesData.images.map((image, index) => {
      const position = generateSpiralPosition(index);

      return {
        ...image,
        x: position.x,
        y: position.y,
        width: 400,
        height: 300,
        zIndex: index
      };
    });

    console.log('Imágenes posicionadas:', positionedImages);
    setImages(positionedImages);
  }, []);

  return { images };
};

export default useCanvas;
